import React from 'react';
import { useLocation } from 'react-router-dom';
import './GameResults.css';

function calculateScore(cards) {
  const cardValues = {
    'Jok': -1,
    'A': 1,
    'J': 11,
    'Q': 12,
    'KS': 13,
    'KC': 13,
    'KD': 0,
    'KH': 0,
  };

  return cards.reduce((total, card) => {
    const value = card.slice(0, -1);
    return total + (cardValues[card] !== undefined ? cardValues[card] : parseInt(value));
  }, 0);
}

function GameResults() {
  const location = useLocation();
  const players = location.state.players;

  const sortedPlayers = players.sort((a, b) => calculateScore(b.cards) - calculateScore(a.cards));

  return (
    <div className="game-results">
      <h2>Game Results</h2>
      <div className="players-grid">
        {sortedPlayers.map((player, index) => (
          <div key={index} className="player-card">
            <img src={player.profileImage} alt={`${player.name}'s profile`} />
            <h3>{player.name}</h3>
            <p>Score: {calculateScore(player.cards)}</p>
            <div className="player-cards">
              {player.cards.map((card, cardIndex) => (
                <div key={cardIndex} className="card">
                  {card}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GameResults;
