import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import LoginPage from './components/LoginPage';
import WaitingLobby from './components/WaitingLobby';
import GameLobby from './components/GameLobby';

const socket = io('http://localhost:4000');

function App() {
  const [username, setUsername] = useState('');
  const [profileImage, setProfileImage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isGameStarted, setIsGameStarted] = useState(false);
  const [players, setPlayers] = useState([]);
  const [initialCards, setInitialCards] = useState([]);

  useEffect(() => {
    socket.on('gameUpdate', ({ players, gameStarted, initialCards }) => {
      setPlayers(players);
      setIsGameStarted(gameStarted);
      setInitialCards(initialCards); // Set the initial cards received from the server
    });

    return () => socket.off('gameUpdate');
  }, []);

  const handleLogin = (name, image) => {
    setUsername(name);
    setProfileImage(image);
    setIsLoggedIn(true);
    socket.emit('joinLobby', { username: name, profileImage: image });
  };

  const handleStartGame = () => {
    socket.emit('startGame');
  };

  return (
    <div className="App">
      {!isLoggedIn && <LoginPage onLogin={handleLogin} />}
      {isLoggedIn && !isGameStarted && <WaitingLobby players={players} onStartGame={handleStartGame} />}
      {isLoggedIn && isGameStarted && <GameLobby players={players} initialCards={initialCards} />}
    </div>
  );
}

export default App;
