import React, { useState, useEffect } from 'react';
import './GameLobby.css';

function GameLobby({ players, initialCards, currentTurn, onNextTurn, remainingDeck }) {
  const [localRemainingDeck, setLocalRemainingDeck] = useState(remainingDeck);
  const [usedCards, setUsedCards] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [topCard, setTopCard] = useState(null);

  useEffect(() => {
    setLocalRemainingDeck(remainingDeck);
  }, [remainingDeck]);

  const handleCardClick = () => {
    if (localRemainingDeck.length > 0) {
      setTopCard(localRemainingDeck[0]);
      setShowPopup(true);
    }
  };

  const handleUsePower = () => {
    const newRemainingDeck = localRemainingDeck.slice(1);
    const newUsedCards = [...usedCards, topCard];

    setLocalRemainingDeck(newRemainingDeck);
    setUsedCards(newUsedCards);
    setShowPopup(false);

    if (newRemainingDeck.length === 0) {
      setLocalRemainingDeck(shuffleDeck(newUsedCards));
      setUsedCards([]);
    }
  };

  const handleReplaceCard = () => {
    setShowPopup(false);
  };

  const renderPlayerSection = (player, index, position) => {
    const cardIndexStart = index * 4;
    const playerCards = initialCards.slice(cardIndexStart, cardIndexStart + 4);
    const isCurrentTurn = currentTurn === index;

    return (
      <div key={index} className={`player-section ${position}`}>
        {position === 'bottom' && (
          <div className="player-hand">
            {playerCards.map((card) => (
              <div key={card} className="card">
                {card}
              </div>
            ))}
          </div>
        )}
        <div className={`player-card ${isCurrentTurn ? 'current-turn' : ''}`}>
          <img src={player.profileImage} alt={`${player.name}'s profile`} />
          <h3>{player.name}</h3>
        </div>
        {position === 'top' && (
          <div className="player-hand">
            {playerCards.map((card) => (
              <div key={card} className="card">
                {card}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  const shuffleDeck = (deck) => {
    let shuffledDeck = [...deck];
    for (let i = shuffledDeck.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffledDeck[i], shuffledDeck[j]] = [shuffledDeck[j], shuffledDeck[i]];
    }
    return shuffledDeck;
  };

  return (
    <div className="game-lobby">
      <h2>Game Lobby</h2>
      <div className="game-content">
        <div className="sidebar">
          <div className="sidebar-cards">
            <div className="sidebar-card" onClick={handleCardClick}>
              Remaining: {localRemainingDeck.length} cards
            </div>
            <div className="sidebar-card">
              {usedCards.length > 0 ? `Used Card: ${usedCards[usedCards.length - 1]}` : 'Card 2'}
            </div>
          </div>
          <div className="sidebar-buttons">
            <button>Button 1</button>
            <button>Button 2</button>
            <button>Button 3</button>
            <button onClick={onNextTurn}>Button 4</button>
          </div>
        </div>
        <div className="game-board">
          <div className="row top-row">
            {topRowPlayers.map((player, index) => renderPlayerSection(player, index, 'top'))}
          </div>
          <div className="row bottom-row">
            {bottomRowPlayers.map((player, index) => renderPlayerSection(player, index + 4, 'bottom'))}
          </div>
        </div>
      </div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Top Card: {topCard}</h3>
            <button onClick={handleUsePower}>Use Power</button>
            <button onClick={handleReplaceCard}>Replace Card</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default GameLobby;
