import React from 'react';
import './GameLobby.css';

function GameLobby({ players, initialCards, currentTurn, onNextTurn }) {
  const topRowPlayers = players.slice(0, 4);
  const bottomRowPlayers = players.slice(4, 8);

  const renderPlayerSection = (player, index, position) => {
    const cardIndexStart = index * 4;
    const playerCards = initialCards.slice(cardIndexStart, cardIndexStart + 4);
    const isCurrentTurn = currentTurn === index;

    return (
      <div key={index} className={`player-section ${position}`}>
        {position === 'bottom' && (
          <div className="player-hand">
            {playerCards.map((card) => (
              <div key={card.id} className="card">
                {card.value}
              </div>
            ))}
          </div>
        )}
        <div className={`player-card ${isCurrentTurn ? 'current-turn' : ''}`}>
          <img src={player.profileImage} alt={`${player.name}'s profile`} />
          <h3>{player.name}</h3>
        </div>
        {position === 'top' && (
          <div className="player-hand">
            {playerCards.map((card) => (
              <div key={card.id} className="card">
                {card.value}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="game-lobby">
      <h2>Game Lobby</h2>
      <div className="game-content">
        <div className="sidebar">
          <div className="sidebar-cards">
            <div className="sidebar-card">Card 1</div>
            <div className="sidebar-card">Card 2</div>
          </div>
          <div className="sidebar-buttons">
            <button>Button 1</button>
            <button>Button 2</button>
            <button>Button 3</button>
            <button onClick={onNextTurn}>Button 4</button>
          </div>
        </div>
        <div className="game-board">
          <div className="row top-row">
            {topRowPlayers.map((player, index) => renderPlayerSection(player, index, 'top'))}
          </div>
          <div className="row bottom-row">
            {bottomRowPlayers.map((player, index) => renderPlayerSection(player, index + 4, 'bottom'))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default GameLobby;
