import React, { useState, useEffect } from 'react';
import './GameLobby.css';
import { io } from 'socket.io-client';

const socket = io('http://localhost:4000');

function GameLobby() {
  const [players, setPlayers] = useState([]);
  const [card1Pile, setCard1Pile] = useState([]);
  const [card2Pile, setCard2Pile] = useState([]);
  const [currentTurn, setCurrentTurn] = useState(0);
  const [showPopup, setShowPopup] = useState(false);
  const [topCard, setTopCard] = useState(null);
  const [allowCardReplacement, setAllowCardReplacement] = useState(false);

  useEffect(() => {
    socket.on('gameUpdate', ({ players, card1Pile, card2Pile, currentTurn }) => {
      setPlayers(players);
      setCard1Pile(card1Pile);
      setCard2Pile(card2Pile);
      setCurrentTurn(currentTurn);
    });

    return () => {
      socket.off('gameUpdate');
    };
  }, []);

  const handleCardClick = () => {
    if (card1Pile.length > 0) {
      setTopCard(card1Pile[card1Pile.length - 1]);
      setShowPopup(true);
    }
  };

  const handleUsePower = () => {
    socket.emit('usePower');
    setShowPopup(false);
  };

  const handleReplaceCard = () => {
    setAllowCardReplacement(true);
    setShowPopup(false);
  };

  const handleCardSelection = (cardIndex) => {
    if (allowCardReplacement) {
      socket.emit('replaceCard', currentTurn, cardIndex);
      setAllowCardReplacement(false);
    }
  };

  const onNextTurn = () => {
    socket.emit('nextTurn');
  };

  return (
    <div className="game-lobby">
      <h2>Game Lobby</h2>
      <div className="game-content">
        <div className="sidebar">
          <div className="sidebar-cards">
            <div className="sidebar-card" onClick={handleCardClick}>
              Remaining: {card1Pile.length} cards
            </div>
            <div className="sidebar-card">
              {card2Pile.length > 0 ? `Card 2: ${card2Pile[card2Pile.length - 1]}` : 'Card 2'}
            </div>
          </div>
          <div className="sidebar-buttons">
            <button>Button 1</button>
            <button>Button 2</button>
            <button>Button 3</button>
            <button onClick={onNextTurn}>Button 4</button>
          </div>
        </div>
        <div className="game-board">
          {players.map((player, playerIndex) => (
            <div key={playerIndex} className="player-section">
              <div className={`player-card ${currentTurn === playerIndex ? 'current-turn' : ''}`}>
                <img src={player.profileImage} alt={`${player.name}'s profile`} />
                <h3>{player.name}</h3>
              </div>
              <div className="player-hand">
                {player.cards.map((card, cardIndex) => (
                  <div
                    key={cardIndex}
                    className={`card ${allowCardReplacement ? 'selectable' : ''}`}
                    onClick={() => allowCardReplacement && handleCardSelection(cardIndex)}
                  >
                    {card}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Top Card: {topCard}</h3>
            <button onClick={handleUsePower}>Use Power</button>
            <button onClick={handleReplaceCard}>Replace Card</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default GameLobby;
